<?php// If it's going to need the database, then it's // probably smart to require it before we start.require_once('database.php');class League_Team extends DatabaseObject {		protected static $table_name = "leagues_teams";	protected static $db_fields=array('leagues_profile_id', 'leagues_teams_id', 'team_name', 'team_captain',		'captain_email',  'captain_phone', 'fee_paid', 'ref_fee_paid', 'team_picture', 'date_added', 'date_updated');	protected $leagues_profile_id;	protected $leagues_teams_id;	public $id;	public $total_players;	public $total_games;	public $complete_games;	public $team_name;	public $team_captain;	public $captain_email;	public $captain_phone;	public $fee_paid;	public $ref_fee_paid;	public $team_picture;	public $date_added;	public $date_updated;		function __construct() {			}	public function get_league_id() {		return $this->leagues_profile_id;	}		public function get_leagues_teams_id() {		return $this->leagues_teams_id;	}	public static function get_all_teams($leaguesID=0) {		$teams = self::find_by_sql("SELECT * FROM leagues_teams WHERE leagues_profile_id = '".$leaguesID."';");		return $teams;	}		public static function get_team_by_id($leaguesID=0, $teamID=0) {		$teams = self::find_by_sql("SELECT * FROM leagues_teams WHERE leagues_profile_id = '".$leaguesID."' AND leagues_teams_id = '".$teamID."';");		return $teams;	}		public static function get_team_players($leaguesID=0, $teamID=0) {		$players = self::find_by_sql("SELECT * FROM leagues_players WHERE leagues_profile_id = '".$leaguesID."' AND leagues_teams_id = '".$teamID."';");		return $players;	}}?>